	@Test
	public void testverifySignature() {
		String signature = ".MDcxOEY3NDEwMTE4ODdERjFCNTk5NUI0MzkyODFCQzAwRDk1OUVENjIwQ0I3OTE5NEMzMjUxRTk4MUM2RjI1MA.hFzxGmtap9JGRLni4nDrUy_gjP8Yp5MOGaiVPDHcvKV6ZNXlWH2-8LgJw2ZMeZo6XPljJkVtxzGOnpZPILZLKYl1dITsVkhqLVm2so-_1LYyfwexoQgsfbDZDlf5UeNipdBLlr4fkd4htQzsZV5p9vMwmhOoVq_TEBXIY7jZ6QWSJx1HioNajaOzc3lwSM8T0zxYxowQD10tGXj5Wdrk21ruf1XDjL5SNzMRVhOZpZR3SpFL19y5orW0CFCiRld7FpfE9NfSHOR61sGLbQL8AMVUgZQuVkkD_W5j_aAXJGZ8wirWcKFnoI7bBZrrAq-vOjH_QUl0jyO7fIl5QPEDcQ";
		Mockito.when(cryptoCore.verifySignature(signature)).thenReturn(true);
		keyServiceManager.verifySignature(signature);		
	}
	
